/*global chrome*/
import React, { Component } from "react";
import SignUp from "./components/SignUp";
import LogIn from "./components/LogIn";
import BlockedCategories from "./components/BlockCategories";
import ForgotPassword from "./components/ForgotPassword";
import Welcome from "./components/Welcome";

class App extends Component {
  state = {
    page: null,
    authUser: localStorage.getItem("authUser")
  };

  handlePages = pageTitle => {
    this.setState({
      page: pageTitle
    });
  };

  handleAuthUser = user => {
    this.setState({
      authUser: user
    });
    localStorage.setItem("authUser", user);
    console.log("user: ", user);
  };

  handleCondition = () => {
    if (this.state.authUser !== null) {
      console.log(this.state.authUser);
      console.log("default welcome page");
      return (
        <Welcome
          changePage={this.handlePages}
          changeAuth={this.handleAuthUser}
        />
      );
    } else {
      return (
        <LogIn changePage={this.handlePages} changeAuth={this.handleAuthUser} />
      );
    }
  };

  componentDidMount() {
    console.log("auth user: ", this.state.authUser);
    console.log("state page: ", this.state.page);
    this.handleCondition();
  }

  renderComponent = () => {
    switch (this.state.page) {
      case "LogIn":
        return (
          <LogIn
            changePage={this.handlePages}
            changeAuth={this.handleAuthUser}
          />
        );
      case "SignUp":
        return <SignUp changePage={this.handlePages} />;
      case "ForgotPassword":
        return <ForgotPassword changePage={this.handlePages} />;
      case "Welcome":
        return (
          <Welcome
            changePage={this.handlePages}
            changeAuth={this.handleAuthUser}
            authUser={this.state.authUser}
          />
        );
      case "BlockedCategories":
        return <BlockedCategories changePage={this.handlePages} />;

      case null:
        console.log("before condition", this.state.authUser);
        setTimeout(this.handleCondition, 2000);

      default:
    }
  };

  render() {
    return <div className="container">{this.renderComponent()}</div>;
  }
}

// export default App;

// function App() {

// }

export default App;
