import app from "firebase/app";
import "firebase/auth";
import "firebase/database";
import "firebase/firestore";

const config = {
  apiKey: "AIzaSyDni4UflIR1JzzSmNdyUfOUIWx9x0nzGM8",
  authDomain: "cat-243915.firebaseapp.com",
  databaseURL: "https://cat-243915.firebaseio.com",
  projectId: "youtube-cat-243915",
  storageBucket: "youtube-cat-243915.appspot.com",
  messagingSenderId: "719371077954",
  appId: "1:719371077954:web:12eebc6a33e42ddc"
};

class Firebase {
  constructor() {
    app.initializeApp(config);
    this.fieldValue = app.firestore.FieldValue;
    this.auth = app.auth();
    this.db = app.firestore();
    this.googleProvider = new app.auth.GoogleAuthProvider();
  }

  //   Authentication
  doCreateUserWithEmailAndPassword = (email, password) =>
    this.auth.createUserWithEmailAndPassword(email, password);

  doSignInWithEmailAndPassword = (email, password) =>
    this.auth.signInWithEmailAndPassword(email, password);

  doSignInWithGoogle = () => this.auth.signInWithPopup(this.googleProvider);

  doSignOut = () => this.auth.signOut();

  doPasswordReset = email => this.auth.sendPasswordResetEmail(email);

  doPasswordUpdate = password => this.auth.currentUser.updatePassword(password);

  user = uid => this.db.doc(`users/${uid}`);

  users = () => this.db.collection("users");
}

export default Firebase;
