import React, { Component } from "react";
import { Input, Button } from "reactstrap";
import { FirebaseContext } from "./firebase";
import { Redirect } from "react-router-dom";

const INITIAL_STATE = {
  email: "",
  password: "",
  retypePassword: "",
  error: null
};

class SignUp extends Component {
  state = {
    ...INITIAL_STATE,
    redirect: false
  };

  handleOnChange = event => {
    this.setState(
      this.setState({
        [event.target.name]: event.target.value
      })
    );
  };

  onSubmit = (firebase, event) => {
    console.log(firebase);
    const { email, password } = this.state;
    firebase.firebase
      .doCreateUserWithEmailAndPassword(email, password)
      .then(authUser => {
        this.setState({ ...INITIAL_STATE, redirect: true });
      })
      .catch(error => {
        this.setState({ error });
      });

    event.preventDefault();
  };

  render() {
    const { email, password, retypePassword, error } = this.state;
    const isInvalid = password === "" || password !== retypePassword;

    // if (this.state.redirect) {
    //   return <Redirect to="/" />;
    // }

    return (
      <FirebaseContext.Consumer>
        {firebase => (
          <div className="container">
            <h1>Sign Up:</h1>
            <Input
              placeholder="Email"
              name="email"
              onChange={this.handleOnChange}
              value={email}
            />
            <Input
              placeholder="Password"
              name="password"
              type="password"
              onChange={this.handleOnChange}
              value={password}
            />
            <Input
              placeholder="Retype Password"
              name="retypePassword"
              type="password"
              onChange={this.handleOnChange}
              value={retypePassword}
            />
            <Button
              disabled={isInvalid}
              color="primary"
              type="submit"
              onClick={event => this.onSubmit({ firebase }, event)}
            >
              Sign Up!
            </Button>{" "}
            <br />
            {error && <p>{error.message}</p>}
            Already have an account? <a href="/">Sign in</a>
          </div>
        )}
      </FirebaseContext.Consumer>
    );
  }
}

export default SignUp;
